import React, { Component } from 'react'
// import unit component Establishment, will want to render on click 
import { Link } from "react-router-dom";
import Appointment from './Appointment'



class Appointments extends Component {
    render() {
        const sortedAppointments = this.props.open_appointments.filter(appointment => appointment.establishment_id == this.props.establishment).sort(dateSort)
        return (
            <div className="appointment-list">
                <h1>Available Dates
                </h1>
                {sortedAppointments.map(a => <Appointment id={a.id} when={a.when} current_user={this.props.current_user}
                requestDate = {this.props.requestDate}/>)}

            </div>
        )
    }
}

export default Appointments

function dateSort(a, b) {

    const dateA = a.when;
    const dateB = b.when;

    let comparison = 0;
    if (dateA > dateB) {
      comparison = 1;
    } else if (dateA < dateB) {
      comparison = -1;
    }
    return comparison;

}
