import React, { Component } from 'react'
// import Establishments from '../components/establishments/Establishments'

import { connect } from 'react-redux'
import { fetchAppointments } from '../actions/fetchAppointments'

class AppointmentContainer extends Component {

    state = {
        open_appointments: []
    }


    componentDidMount() {
        this.props.fetchAppointments()
    }

    componentDidUpdate() {
        this.setState({
            open_appointments: this.props.appointments.filter(appointment => appointment.establishment_id == this.props.establishment)
        })
    }


    render() {
        debugger
        console.log("Appointments Container:")
        console.log(this.props.appointments)
        return (
            <div>
                Appointment Container
            </div>)
    }
}

const mapStateToProps = state => {
    return {
        establishments: state.establishments,
        loading: state.loading,
        current_user: state.current_user,
        appointments: state.appointments
    }
}


const mapDispatchToProps = dispatch => {
    return {
        fetchAppointments: () => dispatch(fetchAppointments())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AppointmentContainer)
