import cuid from 'cuid';
export const cuidFn = cuid;

export default function manageUsers(state = {
    users: []
}, action) {
    switch (action.type) {
        case 'ADD_USER':
            const user = { username: action.username.text, gender: action.gender.text, interests: action.interests.text, id: cuid() }
            return { ...state, users: [...state.users, user] }

        case 'DELETE_USER':
            return { ...state, users: state.users.filter(user => user.id !== action.id)}

        default:
            return state;
    }


}
